import React, { useEffect, useReducer } from "react";
import Input from "./components/Input.js";
import AddRecipe from "./components/AddRecipe.js";
import SearchBooks from "./components/SearchBooks.js";
import BookCard from "./components/BookCard.js";

const initialState = {
    shelfedBook: [],
    recipes: [],
    recipeId: "",
    searchKeyword: "",
    showUI: "show-login",
    user: {}
};
const reducer = (state, action) => {
    switch (action.type) {
        case "search-books":
            return { ...state, bookSearchResult: action.data }
        case "find-books":
            return { ...state, showUI: "show-searched-books" }
        case "shelfed-books":
            return { ...state, shelfedBook: action.data }
        case "load-recipes":
            return { ...state, recipes: action.data };
        case "show-recipe":
            return {
                ...state,
                recipeId: action.recipeId,
                showUI: "recipe-view"
            };

        case"exit-recipe":
            return{
                ...state,
                recipeId: "",
                showUI: "home-view",
                searchKeyword: ""
            };

        case "set-search-keyword":
            return {
                ...state,
                searchKeyword: action.keyword
            };

        case "show-add-recipe":
            return {
                ...state,
                showUI: "add-recipe-view"
            };

        case "exit-add-recipe":
            return {
                ...state,
                showUI: "home-view",
                searchKeyword: ""
            };

        case 'add-recipe':
            return {
                ...state,
                recipes: [...state.recipes, action.recipe],
                showUI: 'home-view',
                searchKeyword: '',
            };

        default:
            throw new Error();
    }
};

const App = () => {
    const [state, dispatch] = useReducer(reducer, initialState);
    const filterRecipes = keyword =>dispatch({ type: "set-search-keyword", keyword });

    useEffect(() => {
        fetch("http://localhost:3010/api/shelf/find?userid=user_1", {mode:'cors'})
        .then(res => res.json())
        .then(data => dispatch(
            { type: "shelfed-books", data }));
    }, []);
  return (
    <div className="recipe-meister">
        <h1>Your Book Shelf</h1>
        {(state.showUI === "show-login") && (
            <div>
                Please login
            </div>
        )}


        {(state.showUI === "home-view") && (

        <div id="home-view">
        <h1>Your Book Shelf</h1>
        <div id="controls">
                <Input
                    label="FIND BOOKS"
                    onInput={evt => filterRecipes(evt.target.value)}
                    value={state.searchKeyword}
                />
                <button
                    className="btn btn-black"
                    onClick={() => dispatch({ type: "find-books" })}
                >
                    SearchBooks
                </button>
        </div>
        <div id="shelfbooks">
        {state.shelfedBook && (
            <div id="home-view">
                {state.shelfedBook.length > 0 &&
                    state.shelfedBook.map(data => (
                        <BookCard key={data.id} data={data} isFav={"true"} />
                    ))}
                </div>
            )}
        </div>  

        </div>
        )}
        {state.showUI === "show-searched-books" && (
            <SearchBooks searchText={state.searchKeyword}/>
        )}
        {state.showUI === "add-recipe-view" && (
            <AddRecipe
                onAdd={recipe => {
                    fetch("/api/recipes", {
                        method: "post",
                        body: JSON.stringify(recipe)
                    })
                    .then(res => res.json())
                    .then(({ recipe }) => {
                        dispatch({ type: "add-recipe", recipe });
                    });
                }}
                onExit={() => dispatch({ type: "exit-add-recipe" })}
            />
        )}
    </div>
  );
};

export default App;
