// Login Component
import React, { useReducer } from "react";

import Input from "./Input";

const initialState = {
    username: "",
    password: "",
    showUI: "",
};
const reducer = (state, action) => {
    switch (action.type) {
        case "set-login-username":
            return { ...state, username: action.data }
        case "set-login-password":
            return { ...state, password: action.data }
        case "set-home-view":
            return { ...state, showUI: action.data }
        default:
            throw new Error();
    }
}

const LoginPage = () => {
    const [state, dispatch] = useReducer(reducer, initialState);
    // const setUsernameValue = keyword => dispatch({ type: "set-login-username", keyword });
    // const setPasswordValue = keyword => dispatch({ type: "set-login-password", keyword });
    const doLogin = () => {
        fetch("http://localhost:3010/api/login?userid="+state.username, {
            mode :'cors',
            method: "POST",
            body: JSON.stringify({"password" : state.password})
        })
        .then(res => console.log(res))
        // .then(res => dispatch(
        //     { type: "set-home-view", data: "home-view" }));
    };
    return (
        <div id="loginform">
           {/* <Input
                    label="UserName"
                    onInput={evt => setUsernameValue(evt.target.value)}
                    value={state.username}
                />

            <Input id="pswd"
                    label="Password"
                    onInput={evt => setPasswordValue(evt.target.value)}
                    value={state.password}
                /> */}

            <button
                    className="btn btn-black"
                    onClick={() => doLogin()}
                >
                    Login
                </button>
        </div>
    );
};


export default LoginPage;
