// AddRecipe Component
import React, {useReducer} from "react";
import Input from "./Input.js";
import InputTags from "./InputTags.js";
import Select from "./Select.js";
import List from "./List.js";
import Text from "./Text.js";
import { v4 as uuidv4 } from "uuid";

const initialState = {
    title: "",
    description: "",
    keywords: [],
    instructions: "",
    ingredients: [],
    dishType: "",
    recipeImg: ""
};

const reducer = (state, action) => {
    switch (action.type) {
        case "update":
            return {
                ...state,
                ...action.payload
            };

        default:
            throw new Error();
    }
}

const AddRecipe = ({ onExit, onAdd }) => {
    const [state, dispatch] = useReducer(reducer, initialState);
    const onSaveHandler = () => {
        const validate = Object.values(state)
            .map(elem => {
                if (
                    typeof elem === "string" && elem === "") {
                    return false;
                }
                if (
                    typeof elem === "object" &&
                    Array.isArray(elem) && 
                    elem.length === 0) 
                    { return false;
                }
                return true;
            })
            .reduce((prev, curr) => prev && curr);
        if (validate) {
            onAdd({ ...state, id: uuidv4() });
        }
    };
    return (
        <div className="add-recipe">
            <h2>Add a Recipe</h2>
            <Input 
                label="Title" 
                onInput={(e) => dispatch({ 
                    type: 'update', 
                    payload: { title: e.target.value } 
                })} 
                value={state.title} 
            />
            <Input 
                label="Description" 
                onInput={(e) => dispatch({ 
                    type: 'update', 
                    payload: { description: e.target.value } 
                })} 
                value={state.description} 
            />
            <Select
                label="Type"
                options={["Vegetarian", "Vegan", "Meat", "Poultry", "Seafood"]}
                onSet={evt =>
                    dispatch({ type: "update", payload: { dishType: evt.target.value } })
                }
            />
            <Select
                label="KeyWord"
                options={["a", "b", "c", "d", "e"]}
                onSet={evt =>
                    dispatch({ type: "update", payload: { keywords:[ { id: uuidv4(), label: evt.target.value } ] } })
                }
            />
            <Input
                label="Image Url"
                onInput={e =>
                    dispatch({ type: "update", payload: { recipeImg: e.target.value } })
                }
                value={state.recipeImg}
            />
            <List 
                label="Ingredients" 
                onUpdate={(items) => dispatch({ 
                    type: 'update', 
                    payload: { ingredients: items } 
                    })} 
                value={state.ingredients} 
            />
            <Text
                label="Instructions"
                onUpdate={instructions =>
                    dispatch({
                        type: "update",
                        payload: {instructions}
                    })
                }
                value={state.instructions}
            />
            <div className="buttons">
                <button className="btn small-btn" onClick={onSaveHandler}>
                    Save
                </button>
                <button className="btn btn-black small-btn" onClick={onExit}>
                    Exit
                </button>
            </div>
        </div>

    );
};

export default AddRecipe;
