import React from "react";
import PropTypes from "prop-types";

const BookCard = ({ data, isFav, user }) => {
    const {volumeInfo , selfLink} = data;
    const {title, description, imageLinks,authors } = volumeInfo;
    let isRemoved = false;
    let isAdded = false;

    const onAdd = (book) => {
        fetch("http://localhost:3010/api/my-books?userid="+user.username+"&bookurl="+book ,{
            mode :'cors',
            method: "POST"
        })
        .then(res => console.log(res))
        .then( isAdded = true)
    };
    const onRemove = (book) => {
        fetch("http://localhost:3010/api/my-books/delete?userid="+user.username+"&bookurl="+book, {
            mode :'cors',
            method: "POST"
        })
        .then(res => console.log(res))
        .then( isRemoved = true)
    };

    return (
        <div className="recipe-card">
            <img className="recipe-img" src={imageLinks?.thumbnail} alt={title} />
            <div className="details">
                <h2>{title}</h2>
                <h3>{description}</h3>
                <div className="keyword" key={authors}>
                            {authors}
                </div>

            </div>
            {isFav === "true" && !isRemoved && (
                    <button className="btn small-btn" onClick={() => onRemove(selfLink)}>
                        Remove
                    </button>
                )}

                {isFav === "true" && isRemoved && (
                    <button className="btn small-btn">
                        Removed
                    </button>
                )}

            {isFav === "false"  &&  !isAdded && (
                <button className="btn small-btn" onClick={ () =>onAdd(selfLink)}>
                    Add to Fav
                </button>
            )}

            {isFav === "false"  &&  isAdded && (
                <button className="btn small-btn">
                    Added
                </button>
            )}
        </div>
    );
};
BookCard.defaultProps = {
    data: {},
    isFav: "false",
};
BookCard.propTypes = {
    data: PropTypes.object.isRequired,
    isFav: PropTypes.string.isRequired
};

export default BookCard;
