import React, { useEffect, useReducer } from "react";
import RecipeCard from "./components/RecipeCard.js";
import Recipe from "./components/Recipe.js";
import Icon from "./components/Icon.js";
import Input from "./components/Input.js";
import AddRecipe from "./components/AddRecipe.js";
import SearchBooks from "./components/SearchBooks.js";

const initialState = {
    recipes: [],
    recipeId: "",
    searchKeyword: "",
    showUI: "home-view"
};
const reducer = (state, action) => {
    switch (action.type) {
        case "search-books":
            return { ...state, bookSearchResult: action.data }
        case "find-books":
            return { ...state, showUI: "show-searched-books" }
        case "load-recipes":
            return { ...state, recipes: action.data };
        case "show-recipe":
            return {
                ...state,
                recipeId: action.recipeId,
                showUI: "recipe-view"
            };

        case"exit-recipe":
            return{
                ...state,
                recipeId: "",
                showUI: "home-view",
                searchKeyword: ""
            };

        case "set-search-keyword":
            return {
                ...state,
                searchKeyword: action.keyword
            };

        case "show-add-recipe":
            return {
                ...state,
                showUI: "add-recipe-view"
            };

        case "exit-add-recipe":
            return {
                ...state,
                showUI: "home-view",
                searchKeyword: ""
            };

        case 'add-recipe':
            return {
                ...state,
                recipes: [...state.recipes, action.recipe],
                showUI: 'home-view',
                searchKeyword: '',
            };

        default:
            throw new Error();
    }
};

const App = () => {
    const [state, dispatch] = useReducer(reducer, initialState);
    const showRecipe = recipeId => dispatch({ type: "show-recipe", recipeId });
    const filterRecipes = keyword =>dispatch({ type: "set-search-keyword", keyword });

    // useEffect(() => {
    //     fetch("http://localhost:3010/api/search-books", {mode:'cors'})
    //     .then(res => res.json())
    //     .then(data => dispatch(
    //         { type: "search-books", data }));
    // }, []);
  return (
    <div className="recipe-meister">
        <h1>Recipe Meister</h1>
        {(state.showUI === "home-view" || state.showUI === "show-searched-books") && (
        <div id="home-view">
        <div id="controls">
                <Input
                    label="FIND BOOKS"
                    onInput={evt => filterRecipes(evt.target.value)}
                    value={state.searchKeyword}
                />
                <button
                    className="btn btn-black"
                    onClick={() => dispatch({ type: "find-books" })}
                >
                    SearchBooks
                </button>
        </div>
        </div>
        )}
        {state.showUI === "show-searched-books" && (
            <SearchBooks searchText={state.searchKeyword}/>
        )}
        {state.showUI === "add-recipe-view" && (
            <AddRecipe
                onAdd={recipe => {
                    fetch("/api/recipes", {
                        method: "post",
                        body: JSON.stringify(recipe)
                    })
                    .then(res => res.json())
                    .then(({ recipe }) => {
                        dispatch({ type: "add-recipe", recipe });
                    });
                }}
                onExit={() => dispatch({ type: "exit-add-recipe" })}
            />
        )}
    </div>
  );
};

export default App;
