import React, {useEffect, useReducer} from "react";


const initialState = {
    bookSearchResult:[],
    showUI: "home-view"
};

const reducer = (state, action) => {
    switch (action.type) {
        case "search-books":
            return { ...state, bookSearchResult: action.data }

        default:
            throw new Error();
    }
}

const SearchBooks = (data) => {
    const [state, dispatch] = useReducer(reducer, initialState);
    useEffect(() => {
        fetch(`http://localhost:3010/api/search-books?search=${data.searchText}`, {mode:'cors'})
        .then(res => res.json())
        .then(data => dispatch(
            { type: "search-books", data }));
    }, [data]);

    return (
        <div className="recipe-meister">
        {state.showUI === "home-view" && state.bookSearchResult.items && (
            <div id="home-view">
                {console.log(state.bookSearchResult.items)}
                {state.bookSearchResult.items.length > 0 &&
                    state.bookSearchResult.items.map(data => (
                        <div key={data.id}> {data.selfLink} </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default SearchBooks;
