import React, { useEffect, useReducer } from "react";
import Input from "./components/Input.js";
import AddRecipe from "./components/AddRecipe.js";
import SearchBooks from "./components/SearchBooks.js";
import BookCard from "./components/BookCard.js";
import LoginPage from "./components/LoginPage.js";

const initialState = {
    shelfedBook: [],
    recipes: [],
    recipeId: "",
    searchKeyword: "",
    showUI: "show-login",
    user: {},
    error: ""
};
const reducer = (state, action) => {
    switch (action.type) {
        case "search-books":
            return { ...state, bookSearchResult: action.data }
        case "find-books":
            return { ...state, showUI: "show-searched-books" }
        case "shelfed-books":
            return { ...state, shelfedBook: action.data }
        case "show-signup-page":
            return { ...state, showUI: "show-signup" }
        case "show-login-page":
                return { ...state, showUI: "show-login" }
        case "success-user-login":
            return {
                ...state,
                showUI: "home-view",
                user: action.data
            };
        case "load-recipes":
            return { ...state, recipes: action.data };
        case "show-recipe":
            return {
                ...state,
                recipeId: action.recipeId,
                showUI: "recipe-view"
            };

        case"exit-recipe":
            return{
                ...state,
                recipeId: "",
                showUI: "home-view",
                searchKeyword: ""
            };

        case "set-search-keyword":
            return {
                ...state,
                searchKeyword: action.keyword
            };

        case "show-add-recipe":
            return {
                ...state,
                showUI: "add-recipe-view"
            };

        case "exit-add-recipe":
            return {
                ...state,
                showUI: "home-view",
                searchKeyword: ""
            };
        case "exit-add-error":
                return {
                    ...state,
                    showUI: "show-login-error",
                    error: "Wrong credentials unable to login !"
                };
        case 'add-recipe':
            return {
                ...state,
                recipes: [...state.recipes, action.recipe],
                showUI: 'home-view',
                searchKeyword: '',
            };

        default:
            throw new Error();
    }
};

const App = () => {
    const [state, dispatch] = useReducer(reducer, initialState);
    const filterRecipes = keyword =>dispatch({ type: "set-search-keyword", keyword });
    console.log(state.user)
    useEffect(() => {
        console.log(state.user)
        if(state.user && state.user.username)
        fetch("http://localhost:3010/api/shelf/find?userid="+state.user.username, {mode:'cors'})
        .then(res => res.json())
        .then(data => dispatch(
            { type: "shelfed-books", data }));
    },[state.user]);
  return (
    <div className="recipe-meister">
        <h1>Your Book Shelf</h1>
        {(state.showUI === "show-login" || state.showUI === "show-login-error") && (
            <div className="box">
            <div id="login">
                Please login
                <LoginPage  onExit={(data) => dispatch({ type: "success-user-login",data })} onError={() => dispatch({ type: "exit-add-error" })}/>
            </div>

            <div id="tosignup" onClick={() => dispatch({ type: "show-signup-page" })}>
               Or Signup
            </div>
            </div>
        )}
        {(state.showUI === "show-login-error") && (
            <div className="error-message">
                {state.error}
            </div>
        )}
        {(state.showUI === "show-signup") && (
            <div>
                <div id="signup">
                Signup Here
                </div>

                <div id="tologin" onClick={() => dispatch({ type: "show-login-page" })}>
               Or Login
            </div>
            </div>

        )}
        {(state.showUI === "home-view") && (
        <div id="home-view">
        <div id="controls">
                <Input
                    label="FIND BOOKS"
                    onInput={evt => filterRecipes(evt.target.value)}
                    value={state.searchKeyword}
                />
                <button
                    className="btn btn-black"
                    onClick={() => dispatch({ type: "find-books" })}
                >
                    SearchBooks
                </button>
        </div>
        <div id="shelfbooks">
        {state.shelfedBook && (
            <div id="home-view">
                {state.shelfedBook.length > 0 &&
                    state.shelfedBook.map(data => (
                        <BookCard key={data.id} data={data} isFav={"true"} user={state.user} />
                    ))}
                </div>
            )}
        </div>  

        </div>
        )}
        {state.showUI === "show-searched-books" && (
            <SearchBooks searchText={state.searchKeyword} userdata={state.user}/>
        )}
        {state.showUI === "add-recipe-view" && (
            <AddRecipe
                onAdd={recipe => {
                    fetch("/api/recipes", {
                        method: "post",
                        body: JSON.stringify(recipe)
                    })
                    .then(res => res.json())
                    .then(({ recipe }) => {
                        dispatch({ type: "add-recipe", recipe });
                    });
                }}
                onExit={() => dispatch({ type: "exit-add-recipe" })}
            />
        )}
    </div>
  );
};

export default App;
