import React, {useEffect, useReducer} from "react";


const initialState = {
    bookSearchResult:[]
};

const reducer = (state, action) => {
    switch (action.type) {
        case "search-books":
            return { ...state, bookSearchResult: action.data }

        default:
            throw new Error();
    }
}

const SearchBooks = () => {
    const [state, dispatch] = useReducer(reducer, initialState);
    useEffect(() => {
        fetch("http://localhost:3010/api/search-books", {mode:'cors'})
        .then(res => res.json())
        .then(data => dispatch(
            { type: "search-books", data }));
    }, []);

    return (
        <div className="recipe-meister">
        {state.showUI === "home-view" && (
            <div id="home-view">
                {state.bookSearchResult.length > 0 &&
                    state.bookSearchResult.map(data => (
                        data
                    ))}
                </div>
            )}
        </div>
    );
};

export default SearchBooks;
