import React from "react";
import PropTypes from "prop-types";

const BookCard = ({ data, isFav }) => {
    const {volumeInfo } = data;
    const {title, description, imageLinks,authors } = volumeInfo;

    const onAdd = (user, book) => {
        // fetch("http://localhost:3010/api/my-books?userid="+user+"&bookurl="+book, {
        //     method: "post"
        // })
        // .then(res => console.log(res))
        console.log("onadd");
    };
    const onRemove = (user, book) => {
        // fetch("http://localhost:3010/api/my-books?userid="+user+"&bookurl="+book, {
        //     method: "delete"
        // })
        // .then(res => console.log(res))
        console.log("onRemove");
    };

    return (
        <div className="recipe-card">
            <img className="recipe-img" src={imageLinks?.thumbnail} alt={title} />
            <div className="details">
                <h2>{title}</h2>
                <h3>{description}</h3>
                <div className="keyword" key={authors}>
                            {authors}
                </div>

            </div>
            {isFav === "true" && (
                    <button className="btn small-btn" onClick={onRemove("user_1",imageLinks?.thumbnail)}>
                        Remove
                    </button>
                )}

            {isFav === "false" && (
                <button className="btn small-btn" onClick={onAdd("user_1",imageLinks?.thumbnail)}>
                    Add to Fav
                </button>
            )}
        </div>
    );
};
BookCard.defaultProps = {
    data: {},
    isFav: "false",
};
BookCard.propTypes = {
    data: PropTypes.object.isRequired,
    isFav: PropTypes.string.isRequired
};

export default BookCard;
